sys_script_include.script:
var NeedItUtilsChallenge = Class.create();
NeedItUtilsChallenge.prototype = {
    initialize: function() {
    },

	// Challenge : Do not allow same-day requests.
	// We are going to call this Script Include in the NeedIt when needed field date business rule.
	// Created a isDateToday function and create GlideDate Time objects for current date and passed in date.
	isDateToday: function(strDate1){
		var datecurrent = new GlideDateTime();
		var istoday = new GlideDateTime(strDate1);
		//Get the date portion of datecurrent and istoday
		var today = datecurrent.getLocalDate();
		var istodaydate = istoday.getLocalDate();

		if(today.compareTo(istodaydate)==0){
			return true;
		}
		else{
			return false;
		}

	},
    type: 'NeedItUtilsChallenge'
};
